it', 'open', 'open', '', '1289-revision-9', '', '', '2011-03-17 20:12:55', '2011-03-17 19:12:55', '', 1289, 'http://localhost:8888/benmerde2012/2011/03/1289-revision-9/', 0, 'revision', '', 0),
